(function($){var methods={init:function(options){var settings=$.extend({notes:'#quickview-notes',alerts:'#quickview-alerts',chat:'#quickview-chat',notesList:'.list',notesBody:'.note',},options);$(settings.notes).on('click','.list > ul > li',function(e){slide('left');});$(settings.notes).on('click','.list > ul > li .checkbox',function(e){e.stopPropagation();});$(settings.notes).on('click','.close-note-link',function(e){slide('right');});$('.delete-note-link').click(function(){$(this).toggleClass('selected');$(settings.notes).find('.list > ul > li .checkbox').fadeToggle("fast");$('.btn-remove-notes').fadeToggle("fast");});$('.new-note-link').click(function(e){slide('left',function(){console.log("Clear notes");});});$('.btn-remove-notes').click(function(){var checked=$(settings.notes).find('input[type=checkbox]:checked');checked.each(function(){$(this).parents('li').remove();});});$(settings.notes).on('click','.toolbar > li > a',function(){var btnClass=$(this).attr('class');$('.js-editor-buttons').find('.'+btnClass).click();$(this).toggleClass('active');});function slide(side,callback){$(settings.notes+' > .inner').toggleClass('slided');if(typeof callback!='undefined'){callback();}}},show:function(){},hide:function(){},update:function(content){}};$.fn.pagesQuickView=function(methodOrOptions){if(methods[methodOrOptions]){return methods[methodOrOptions].apply(this,Array.prototype.slice.call(arguments,1));}else if(typeof methodOrOptions==='object'||!methodOrOptions){return methods.init.apply(this,arguments);}else{$.error('Method '+ methodOrOptions+' does not exist on jQuery.Pages Sidebar ');}};})(jQuery);